mapper.select:
set @success := 0; set @fail := 0; select DATE_FORMAT(last_time,"%Y-%m-%d %H:%i:%s") as 'times', max(@success := @success + job_finished_count) as 'successJobTotal', max(@fail := @fail + job_exception_count) as 'failJobTotal' from (select job_exception_count, job_finished_count, last_time from skyworld_portal_stat where last_time >= str_to_date(#{startTime,jdbcType=VARCHAR},"%Y-%m-%d %H:%i:%s") and last_time <= str_to_date(#{endTime,jdbcType=VARCHAR},"%Y-%m-%d %H:%i:%s")
and scheduler = #{scheduler,jdbcType=VARCHAR}
order by last_time) t group by last_time

set @success := 0; set @fail := 0; select DATE_FORMAT(last_time,"%Y-%m-%d %H:%i:%s") as 'times', max(@success := @success + task_finished_count) as successTaskTotal, max(@fail := @fail + task_exception_count) as failTaskTotal from (select task_exception_count, task_finished_count, last_time from skyworld_portal_stat where last_time >= str_to_date(#{startTime,jdbcType=VARCHAR},"%Y-%m-%d %H:%i:%s") and last_time <= str_to_date(#{endTime,jdbcType=VARCHAR},"%Y-%m-%d %H:%i:%s")
and scheduler = #{scheduler,jdbcType=VARCHAR}
order by last_time) t group by last_time
